import React from 'react'
import { useQuery, gql } from '@apollo/client'

const BOOKS = gql`
	query GetBooks {
		books {
			name
		}
	}
`

function BookList() {
	const { loading, error, data } = useQuery(BOOKS)

	if (loading) return <p>Loading...</p>
	if (error) return <p>Uh oh. Something went wrong :(</p>

	return (
		<ul>
			<li>Hello</li>
		</ul>
	)
}

export default BookList
