import React from 'react'
import { useQuery, gql } from '@apollo/client'

const GET_AUTHORS = gql`
	query GetAuthors {
		authors {
			name
			id
		}
	}
`

const AuthorPopup = ({ show }) => {
	return (
		<select className={'six columns ' + (show ? 'shown' : 'hidden')}>
			<option value="this" key="this">
				This
			</option>
		</select>
	)
}

export default AuthorPopup
